package com.netease.vcloud.vod.api.listener;import javax.servlet.ServletContextEvent;import javax.servlet.ServletContextListener;import com.netease.vcloud.framework.SysConfigLoader;import com.netease.vcloud.vod.api.util.AdminWebConfigLoader;public class AdminConfigurationListener implements ServletContextListener {	public static int MINUTE = 1000 * 60;	public void contextDestroyed(ServletContextEvent arg0) {	}	public void contextInitialized(ServletContextEvent arg0) {		AdminWebConfigLoader config = new AdminWebConfigLoader();				SysConfigLoader loader = new SysConfigLoader(config);		loader.start();	}}